import * as React from 'react';
import * as styles from '../css/main.scss';
import { Container, IContainer } from './Container';

interface IImage extends IContainer {
  badge?: boolean;
  fullWidth?: boolean;
  src?: string;
  alt?: any;
  variant?: 'logo' | 'logo alt';
}

interface IState {
  showAlt: boolean;
}

export class Image extends React.Component<IImage, IState> {
  constructor(props: IImage) {
    super(props);

    this.state = { showAlt: false };
    this.onError = this.onError.bind(this);
  }

  public render() {
    const classes: string[] = [styles.imageResponsive];
    let src = this.props.src;
    if (this.props.variant) {
      switch (this.props.variant) {
        case 'logo alt':
          src =
            'data:image/png;base64,';
          break;
        case 'logo':
          src =
            'data:image/png;base64,';
          break;
      }
    }
    return (
      <>
        {this.state.showAlt && (
          <Container className={styles.image} {...this.props}>
            {this.props.alt}
          </Container>
        )}
        {!this.state.showAlt && !this.props.badge && (
          <Container className={styles.image} {...this.props}>
            <img onError={this.onError} className={classes.join(' ')} src={src} />
          </Container>
        )}
        {!this.state.showAlt && this.props.badge && (
          <Container
            className={styles.image}
            {...this.props}
            backgroundImage={'url("' + src + '")'}
            backgroundPosition={'center'}
            backgroundSize={'cover'}
          />
        )}
      </>
    );
  }

  public onError() {
    if (this.props.alt) {
      this.setState({ showAlt: true });
    }
  }
}
